export function ConfirmPasswordValidator(controlName, matchControlName) {
  return formGroup => {
    const passwordControl = formGroup.controls[controlName];
    const confirmPasswordControl = formGroup.controls[controlName];

    if (confirmPasswordControl.errors && confirmPasswordControl.errors['confirmPasswordValidator']) {
      return;
    }

    if (passwordControl.value !== confirmPasswordControl.value) {
      confirmPasswordControl.setErrors({
        confirmPasswordValidator: true
      });
    } else {
      confirmPasswordControl.setErrors(null);
    }
  };
}
